#! /usr/bin/env nix-shell
#! nix-shell -i bash -p bash ncurses pv

# This script will update all of the sources.json files generated by niv

green="$(tput setaf 2)"
yellow="$(tput setaf 3)"
bold="$(tput bold)"
reset="$(tput sgr 0)"
chars="/-\|"

find . -type f -name "update-sources.sh" -exec readlink -f {} \; | while read -r updatescript; do
  (
    cd "$(dirname -- "$updatescript")" || exit
    (
      while :; do
        for (( i=0; i<${#chars}; i++ )); do
          sleep 0.1
          echo -en "[${chars:$i:1}]" ""$yellow"Running "$bold""$green""$(pwd)/update-sources.sh""$reset"..." "\r"
        done
      done &
      SPIN_PID=$!
      trap "kill -9 $SPIN_PID" `seq 0 15`
      TEMP=$(mktemp)
      ./update-sources.sh > "$TEMP"
      echo -e "\n"
      cat "$TEMP"
      echo
    )
  )
done
